CreateConVar("omniman_cfg_defaulthealth", "30000", {FCVAR_ARCHIVE, FCVAR_REPLICATED, FCVAR_PROTECTED, FCVAR_SERVER_CAN_EXECUTE} )
CreateConVar("omniman_cfg_superarmormul", "1", {FCVAR_ARCHIVE, FCVAR_REPLICATED, FCVAR_PROTECTED, FCVAR_SERVER_CAN_EXECUTE} )
CreateConVar("omniman_cfg_regenenabled", "1", {FCVAR_ARCHIVE, FCVAR_REPLICATED, FCVAR_PROTECTED, FCVAR_SERVER_CAN_EXECUTE} )

