CreateClientConVar("omniman_debris_enabled", 1, true, false, "Toggle debris", 0, 1)
CreateClientConVar("omniman_ldm_enabled", 0, true, false, "Low Detail Mode", 0, 1)
CreateClientConVar("omniman_debris_lifetime", 3, true, false, "Low Detail Mode", 0, 10000)

if CLIENT then
    concommand.Add("omniman_cleardebris", function( ply, cmd, args )
        for k, ent in ents.Iterator() do
            if ent.TableIndex and gebLib__DebrisList[ent.TableIndex] then
                gebLib_utils.RemoveDebris(ent)
                continue
            end
            if (ent:GetClass() == "geblib_decal") then
                ent:Remove()
            end
        end
        chat.AddText(Color(200, 0, 0), "[OMNI-MAN SWEP] ", color_white, "Debris removed")
    end)
end

