hook.Add("PopulateToolMenu", "OmniMan.Spawnmenu.Settings", function()
    spawnmenu.AddToolMenuOption("Options", "Omni-Man SWEP", "OmniManSWEP_Config", language.GetPhrase("character.omni_man.settings"), "", "", function(panel)
        panel:ClearControls()

        panel:NumSlider(language.GetPhrase("character.omni_man.settings.superflight_sensitivity"), "omniman_cfg_superflight_sensitivity", 0, 1, 2)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings.superflight_sensitivity_hint"))
        panel:NumSlider(language.GetPhrase("character.omni_man.settings.damagemul"), "omniman_cfg_damagemul", 0, 10, 2)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings.damagemul_hint"))
        panel:NumSlider(language.GetPhrase("character.omni_man.settings.health"), "omniman_cfg_defaulthealth", 100, 100000, 0)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings.health_hint"))
        panel:NumSlider(language.GetPhrase("character.omni_man.settings.superarmor"), "omniman_cfg_superarmormul", 0.001, 10, 2)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings.superarmor_hint"))
        panel:CheckBox(language.GetPhrase("character.omni_man.settings.regenenabled"), "omniman_cfg_regenenabled", 0.001, 10, 2)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings.regenenabled_hint"))
    end)

    spawnmenu.AddToolMenuOption("Options", "Omni-Man SWEP", "OmniManSWEP_ConfigCL", language.GetPhrase("character.omni_man.settings_cl"), "", "", function(panel)
        panel:ClearControls()

        panel:CheckBox(language.GetPhrase("character.omni_man.settings_cl.toggledebris"), "omniman_debris_enabled")
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings_cl.toggledebris_hint"))
        panel:CheckBox(language.GetPhrase("character.omni_man.settings_cl.ldm"), "omniman_ldm_enabled")
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings_cl.ldm_hint"))
        panel:CheckBox(language.GetPhrase("character.omni_man.settings_cl.vo_enabled"), "omniman_voicelines_enabled")
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings_cl.vo_enabled_hint"))
        panel:NumSlider(language.GetPhrase("character.omni_man.settings_cl.debrislifetime"), "omniman_debris_lifetime", 0.001, 100, 2)
        panel:ControlHelp(language.GetPhrase("character.omni_man.settings_cl.debrislifetime_hint"))
        panel:Button(language.GetPhrase("character.omni_man.settings_cl.cleardebris"), "omniman_cleardebris")
    end)
end)